<style  scoped>
.felipe-container {
  display: flex;
  font-family: Roboto, sans-serif;
  font-weight: 800;
  font-size: 200px;
}
.dot {
  color: lightblue;
}
.letter,.lette8 {
  display: none;
  position: absolute;
  left: 80px;
}
.letter6 {
}
@media only screen and (max-width: 1024px){
  .letras{
    display: flex;
    justify-content: center;
}
}
</style>

<script>
export default {
  data() {
    return {
      letters: [],
    };
  },
  mounted() {
      this.changeDisplay();   
  },
  methods: {
    showSixthDiv() {

      var sixthDiv = document.querySelector(".lette8");
      var count = 0;
      // Define um intervalo para mostrar e ocultar a sexta div
      var interval = setInterval(function () {
        if (count % 2 === 0) {
          sixthDiv.style.display = "block"; // Mostra a sexta div
        } else {
          sixthDiv.style.display = "none"; // Oculta a sexta div
        }
        count++;

        // Quando a sexta div foi mostrada e ocultada 3 vezes, limpa o intervalo
        if (count === 6) {
          clearInterval(interval);
          // Inicia a animação novamente após 2 segundos
        }
      }, 500);    
    },
    changeDisplay() {
      var divs = document.querySelectorAll(".letter");
      var index = 0;

      // Define um intervalo para mostrar cada div
      var interval = setInterval(function () {
        // Esconde a div anterior (se existir)
        if (index > 0) {
          divs[index - 1].style.display = "none";
        }

        // Mostra a próxima div
        divs[index].style.display = "block";

        // Incrementa o índice para a próxima div
        index++;

        // Verifica se todas as divs foram mostradas
        if (index >= divs.length) {
        
          clearInterval(interval); // Para o intervalo
          //setTimeout(this.changeDisplay(), 2000);
        }
      }, 150); // Intervalo de 2 segundos
      

      this.showSixthDiv()

    },
  },
};
</script>